/** biome-ignore-all lint/suspicious/noArrayIndexKey: <explanation> */
import { useTranslations } from "next-intl";
import { Marquee, MarqueeContent, MarqueeItem } from "./kibo-ui/marquee";

export function TechStack() {
  const t = useTranslations("main");
  const technologies = [
    { icon: ReactIcon },
    { icon: TypeScriptIcon },
    { icon: TailwindIcon },
    { icon: NextJsIcon },
    { icon: JavaScriptIcon },
    { icon: Html5Icon },
    { icon: Css3Icon },
    { icon: GitIcon },
  ];

  return (
    <section
      className="flex h-full flex-col items-start justify-between space-y-6 py-8"
      id="about"
    >
      <h2 className="px-6 font-bold text-2xl text-foreground">
        {t("tech_stack")}
      </h2>
      <Marquee className="">
        <MarqueeContent>
          {[...technologies, ...technologies].map((tech, index) => (
            <MarqueeItem
              className="mx-6 flex h-fit min-w-fit items-center justify-center"
              key={index}
            >
              <tech.icon />
            </MarqueeItem>
          ))}
        </MarqueeContent>
      </Marquee>
      <Marquee className="mb-8">
        <MarqueeContent direction="right">
          {[...technologies, ...technologies].map((tech, index) => (
            <MarqueeItem
              className="mx-6 flex h-fit min-w-fit items-center justify-center"
              key={index}
            >
              <tech.icon />
            </MarqueeItem>
          ))}
        </MarqueeContent>
      </Marquee>
    </section>
  );
}

